mapper.insert:
INSERT INTO wiper.order ( order_id, customer_id, date_of_payment, amount_of_payment, basic_charge, tax_percent, mode_of_payment, transaction_status_id, comment ) VALUES ( #{order.orderId}, #{order.customerId}, #{order.dateOfPayment}, #{order.amountOfPayment}, #{order.basicCharge}, #{order.taxPercent}, #{order.modeOfPayment}, #{order.transactionStatus}, #{order.comment} ) ON DUPLICATE KEY UPDATE customer_id = IF(#{order.customerId} IS NOT NULL, #{order.customerId}, customer_id), date_of_payment = IF(#{order.dateOfPayment} IS NOT NULL, #{order.dateOfPayment}, date_of_payment), amount_of_payment = IF(#{order.amountOfPayment} IS NOT NULL, #{order.amountOfPayment}, amount_of_payment), basic_charge = IF(#{order.basicCharge} IS NOT NULL, #{order.basicCharge}, basic_charge), tax_percent = IF(#{order.taxPercent} IS NOT NULL, #{order.taxPercent}, tax_percent), mode_of_payment = IF(#{order.modeOfPayment} IS NOT NULL, #{order.modeOfPayment}, mode_of_payment), transaction_status_id = IF(#{order.transactionStatus} IS NOT NULL, #{order.transactionStatus}, transaction_status_id), comment = IF(#{order.comment} IS NOT NULL, #{order.comment}, comment);
select #{order.orderId};
SELECT LAST_INSERT_ID() as orderId;
