mapper.insert:
INSERT INTO payment ( payment_id, customer_id, date_of_payment, amount_of_payment, basic_charge, tax_percent, mode_of_payment, transaction_status_id, comment ) VALUES ( #{payment.paymentId}, #{payment.customerId}, #{payment.dateOfPayment}, #{payment.amountOfPayment}, #{payment.basicCharge}, #{payment.taxPercent}, #{payment.modeOfPayment}, #{payment.transactionStatus}, #{payment.comment} ) ON DUPLICATE KEY UPDATE payment_id = IF(#{payment.paymentId} IS NOT NULL, #{payment.paymentId}, payment_id), customer_id = IF(#{payment.customerId} IS NOT NULL, #{payment.customerId}, customer_id), date_of_payment = IF(#{payment.dateOfPayment} IS NOT NULL, #{payment.dateOfPayment}, date_of_payment), amount_of_payment = IF(#{payment.amountOfPayment} IS NOT NULL, #{payment.amountOfPayment}, amount_of_payment), basic_charge = IF(#{payment.basicCharge} IS NOT NULL, #{payment.basicCharge}, basic_charge), tax_percent = IF(#{payment.taxPercent} IS NOT NULL, #{payment.taxPercent}, tax_percent), mode_of_payment = IF(#{payment.modeOfPayment} IS NOT NULL, #{payment.modeOfPayment}, mode_of_payment), transaction_status_id = IF(#{payment.transactionStatus} IS NOT NULL, #{payment.transactionStatus}, transaction_status_id), comment = IF(#{payment.comment} IS NOT NULL, #{payment.comment}, comment);
select #{payment.paymentId};
SELECT LAST_INSERT_ID() as paymentId;
