mapper.insert:
INSERT INTO service_details ( service_detail_id, vehicle_id, service_type_id, subscription_start_date, subscription_end_date, buffer_days_availed, is_active ) VALUES ( #{serviceDetails.serviceDetailId}, #{serviceDetails.vehicleId}, #{serviceDetails.service.serviceTypeId}, #{serviceDetails.subscriptionStartDate}, #{serviceDetails.subscriptionEndDate}, #{serviceDetails.bufferDaysAvailed}, #{serviceDetails.isActive} ) ON DUPLICATE KEY UPDATE vehicle_id = IF(#{serviceDetails.vehicleId} IS NOT NULL, #{serviceDetails.vehicleId}, vehicle_id), service_type_id = IF(#{serviceDetails.service.serviceTypeId} IS NOT NULL, #{serviceDetails.service.serviceTypeId}, service_type_id), subscription_start_date = IF(#{serviceDetails.subscriptionStartDate} IS NOT NULL, #{serviceDetails.subscriptionStartDate}, subscription_start_date), subscription_end_date = IF(#{serviceDetails.subscriptionEndDate} IS NOT NULL, #{serviceDetails.subscriptionEndDate}, subscription_end_date), buffer_days_availed = IF(#{serviceDetails.bufferDaysAvailed} IS NOT NULL, #{serviceDetails.bufferDaysAvailed}, buffer_days_availed), is_active = IF(#{serviceDetails.isActive} IS NOT NULL, #{serviceDetails.isActive}, is_active);
select #{serviceDetails.serviceDetailId};
SELECT LAST_INSERT_ID() as serviceDetailId;
